import React, { useState } from 'react';
import './Experience.css';

function Experience() {
  const [activeTab, setActiveTab] = useState(0);

  const handleTabClick = (index) => {
    setActiveTab(index);
  };

  return (
    <div id="experience">
      <div className="fade-in-section is-visible">
        <div className="section-header">
          <span className="section-title">/ experience</span>
        </div>
        <div className="experience-container">
          <div className="experience-tabs">
            <div className="experience-list">
              {/* Experience Tabs */}
              <button 
                className={`experience-tab ${activeTab === 0 ? 'active' : ''}`} 
                onClick={() => handleTabClick(0)}
              >
                Pricerite Cash & Carry
              </button>
              <button 
                className={`experience-tab ${activeTab === 1 ? 'active' : ''}`} 
                onClick={() => handleTabClick(1)}
              >
                Luciano Men’s Boutique
              </button>
              <button 
                className={`experience-tab ${activeTab === 2 ? 'active' : ''}`} 
                onClick={() => handleTabClick(2)}
              >
                Internships
              </button>
            </div>
          </div>

          <div className="experience-details">
            {/* Experience Content */}
            {activeTab === 0 && (
              <div className="experience-content">
                <h3>Business Analyst @ Pricerite Cash & Carry</h3>
                <div className="job-duration">July 2022 - July 2023</div>
                <ul className="job-description">
                  <li>Spearheaded market research initiatives, analyzing industry data to uncover 15% growth opportunities in emerging food product categories.</li>
                  <li>Implemented inventory optimization strategies, reducing carrying costs by 20% and improving supply chain efficiency by 25%.</li>
                </ul>
              </div>
            )}
            {activeTab === 1 && (
              <div className="experience-content">
                <h3>Assistant Manager @ Luciano Men’s Boutique</h3>
                <div className="job-duration">January 2018 - August 2019</div>
                <ul className="job-description">
                  <li>Supervised daily store operations, resulting in a 10% increase in repeat business by introducing personalized shopping experiences.</li>
                  <li>Optimized merchandising layouts and implemented a real-time inventory tracking system, reducing stock discrepancies by 15%.</li>
                </ul>
              </div>
            )}
            {activeTab === 2 && (
              <div className="experience-content">
                <h3>Business Analyst Intern @ Pricerite Cash & Carry</h3>
                <div className="job-duration">July 2021 - September 2021</div>
                <ul className="job-description">
                  <li>Assisted in developing data-driven customer segmentation models, supporting targeted marketing efforts.</li>
                  <li>Conducted competitor analysis, leading to a 7% increase in market share within key food product segments.</li>
                </ul>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Experience;
