import React, { useState } from 'react';
import './Experience.css';

function Experience() {
  const [activeTab, setActiveTab] = useState(0);

  const experiences = [
    {
      company: 'Pricerite Cash & Carry',
      title: 'Business Analyst',
      duration: 'July 2022 - July 2023',
      details: [
        'Spearheaded market research initiatives, analyzing industry data to uncover 15% growth opportunities in emerging food product categories.',
        'Implemented inventory optimization strategies, reducing carrying costs by 20% and improving supply chain efficiency by 25%.',
      ],
    },
    {
      company: 'Luciano Men’s Boutique',
      title: 'Data Consultant',
      duration: 'April 2021 - June 2022',
      details: [
        'Advised on inventory and pricing strategy, improving sales by 10% through data analysis.',
        'Developed tools for better demand forecasting and customer segmentation.',
      ],
    },
    {
      company: 'Internships',
      title: 'Business Analytics Intern',
      duration: '2020 - 2021',
      details: [
        'Worked with various data sets to analyze market trends and customer behavior, delivering actionable insights for the team.',
        'Collaborated on designing a reporting dashboard for better tracking of KPIs.',
      ],
    },
  ];

  return (
    <div id="experience" className="experience-container fade-in-section is-visible">
      <div className="section-header">
        <span className="section-title">/ experience</span>
      </div>
      <div className="experience-content">
        {/* Left side: Company names (Vertical Tabs) */}
        <div className="experience-tabs MuiTabs-vertical">
          {experiences.map((exp, index) => (
            <button
              key={index}
              className={`tab-button ${activeTab === index ? 'active' : ''}`}
              onClick={() => setActiveTab(index)}
            >
              <span>{exp.company}</span>
            </button>
          ))}
        </div>

        {/* Right side: Experience details */}
        <div className="experience-details">
          <h3>{experiences[activeTab].title} @ <span className="company-name">{experiences[activeTab].company}</span></h3>
          <p className="job-duration">{experiences[activeTab].duration}</p>
          <ul className="job-description">
            {experiences[activeTab].details.map((detail, index) => (
              <li key={index} className="fade-in-section" style={{ transitionDelay: `${100 * (index + 1)}ms` }}>
                {detail}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Experience;
