import React, { useState, useEffect } from 'react';
import './Experience.css';

function Experience() {
  const [selectedTab, setSelectedTab] = useState(0);
  const [visible, setVisible] = useState(false);

  const experiences = [
    {
      company: 'Pricerite Cash & Carry',
      role: 'Business Analyst @ Pricerite Cash & Carry',
      date: 'July 2022 - July 2023',
      description: [
        'Spearheaded market research initiatives, analyzing industry data to uncover 15% growth opportunities in emerging food product categories.',
        'Implemented inventory optimization strategies, reducing carrying costs by 20% and improving supply chain efficiency by 25%.'
      ]
    },
    {
      company: 'Luciano Men’s Boutique',
      role: 'Retail Manager @ Luciano Men’s Boutique',
      date: '2021 - 2022',
      description: [
        'Managed retail operations, ensuring optimal customer satisfaction.',
        'Increased sales revenue by 10% through targeted marketing campaigns.'
      ]
    },
    {
      company: 'Internships',
      role: 'Business Analytics Intern @ Internships',
      date: '2020 - 2021',
      description: [
        'Assisted in developing data-driven marketing strategies.',
        'Analyzed customer data to improve retention rates by 12%.'
      ]
    }
  ];

  useEffect(() => {
    setVisible(true);
  }, [selectedTab]);

  return (
    <div className="experience-container">
      <h2>/ experience</h2>
      <div className="experience-content">
        <div className="company-list">
          {experiences.map((exp, index) => (
            <button
              key={index}
              className={`company-item ${selectedTab === index ? 'active' : ''}`}
              onClick={() => {
                setVisible(false);
                setTimeout(() => setSelectedTab(index), 300);
              }}
            >
              {exp.company}
            </button>
          ))}
        </div>
        <div className={`experience-detail ${visible ? 'fade-in' : 'fade-out'}`}>
          <h3>{experiences[selectedTab].role}</h3>
          <p>{experiences[selectedTab].date}</p>
          <ul>
            {experiences[selectedTab].description.map((item, idx) => (
              <li key={idx}>{item}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Experience;
