import React, { useState, useEffect } from 'react';
import './Experience.css';

function Experience() {
  const [activeTab, setActiveTab] = useState(0);
  const jobs = [
    {
      company: "Pricerite Cash & Carry",
      title: "Business Analyst",
      duration: "July 2022 - July 2023",
      responsibilities: [
        "Spearheaded market research initiatives, analyzing industry data to uncover 15% growth opportunities in emerging food product categories.",
        "Implemented inventory optimization strategies, reducing carrying costs by 20% and improving supply chain efficiency by 25%."
      ]
    },
    {
      company: "Luciano Men’s Boutique",
      title: "Business Manager",
      duration: "May 2020 - June 2022",
      responsibilities: [
        "Developed and managed a digital transformation strategy for the business, increasing online sales by 40%.",
        "Oversaw day-to-day operations, optimizing processes and improving customer satisfaction by 30%."
      ]
    },
    {
      company: "Internships",
      title: "Data Science Intern",
      duration: "Various (2020 - 2021)",
      responsibilities: [
        "Worked on various internships, helping to analyze datasets and visualize insights using Python and SQL.",
        "Assisted in building machine learning models to predict key business outcomes."
      ]
    }
  ];

  // To handle fade-in animation
  useEffect(() => {
    const fadeInSection = document.querySelectorAll('.fade-in-section');
    const options = {
      threshold: 0.25
    };

    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          entry.target.classList.add('is-visible');
        }
      });
    }, options);

    fadeInSection.forEach((section) => {
      observer.observe(section);
    });

    return () => {
      fadeInSection.forEach((section) => {
        observer.unobserve(section);
      });
    };
  }, []);

  return (
    <div className="experience-container fade-in-section">
      <h2>/ experience</h2>
      <div className="experience-tabs">
        {jobs.map((job, index) => (
          <button
            key={index}
            className={index === activeTab ? 'active' : ''}
            onClick={() => setActiveTab(index)}
          >
            {job.company}
          </button>
        ))}
      </div>
      <div className="experience-content">
        <h3>{jobs[activeTab].title} @ <span className="company">{jobs[activeTab].company}</span></h3>
        <div className="duration">{jobs[activeTab].duration}</div>
        <ul>
          {jobs[activeTab].responsibilities.map((resp, idx) => (
            <li key={idx}>{resp}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Experience;
