import React, { useState } from 'react';
import './Experience.css';

function Experience() {
  const [selectedTab, setSelectedTab] = useState(0);

  const experiences = [
    {
      company: "Pricerite Cash & Carry",
      role: "Business Analyst",
      date: "July 2022 - July 2023",
      details: [
        "Spearheaded market research initiatives, analyzing industry data to uncover 15% growth opportunities in emerging food product categories.",
        "Implemented inventory optimization strategies, reducing carrying costs by 20% and improving supply chain efficiency by 25%."
      ]
    },
    {
      company: "Luciano Men’s Boutique",
      role: "Business Analyst Intern",
      date: "2021 - 2022",
      details: [
        "Led market analysis and developed strategies to improve in-store and online sales by 10%.",
        "Optimized product listing and increased customer engagement through social media strategies."
      ]
    },
    {
      company: "Internships",
      role: "Business Analytics Intern",
      date: "2020 - 2021",
      details: [
        "Collaborated with cross-functional teams to analyze and visualize key metrics.",
        "Improved internal data reporting processes, reducing reporting time by 15%."
      ]
    }
  ];

  return (
    <div className="experience-container">
      <h2 className="section-title">/ experience</h2>
      <div className="experience-content">
        <div className="experience-tabs">
          {experiences.map((exp, index) => (
            <button
              key={index}
              className={`tab-button ${selectedTab === index ? 'active' : ''}`}
              onClick={() => setSelectedTab(index)}
            >
              {exp.company}
            </button>
          ))}
        </div>
        <div className="experience-details">
          <h3>{experiences[selectedTab].role} @ <span className="company-name">{experiences[selectedTab].company}</span></h3>
          <div className="job-duration">{experiences[selectedTab].date}</div>
          <ul>
            {experiences[selectedTab].details.map((detail, i) => (
              <li key={i}>{detail}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Experience;
